import React, { useEffect } from "react";
import Navbar from './Navbar';
import { VStack } from "@chakra-ui/react";

import {
  BrowserRouter as Router,
  Route,
  Routes,
  useLocation,
} from "react-router-dom";




function ScrollToTop() {
  const { pathname } = useLocation();

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  return null;
}


function App() {

  return (
    <div>
      <Navbar />

      <VStack>
        <Router>
          <ScrollToTop />

          <div className="App" >

            <Routes>
              {/* Routes */}



            </Routes>
          </div>

        </Router>
      </VStack>

    </div>
  );
}

export default App;
